import PropTypes from 'prop-types';
import React from 'react';
import {
    btnSizes, // deprecated
    btnStyles
} from './constants';
import deprecate from './deprecate';
import styles from './styles/index.styl';

/**
 * @example ../examples/Button.md
 */
const Button = ({
    className,
    tag: Component,
    type,
    btnSize, // deprecated
    lg,
    md,
    sm,
    xs,
    btnStyle,
    outline,
    block,
    active,
    hover,
    focus,
