        const remappedPropName = 'lg|md|sm|xs';

        deprecate({ deprecatedPropName, remappedPropName });

        lg = (btnSize === 'large' || btnSize === 'lg');
        md = (btnSize === 'medium' || btnSize === 'md');
        sm = (btnSize === 'small' || btnSize === 'sm');
        xs = (btnSize === 'extra-small' || btnSize === 'xs');
    }

    if (lg) {
        md = false;
        sm = false;
        xs = false;
    }
    if (md) {
        sm = false;
        xs = false;
    }
    if (sm) {
        xs = false;
