import React, { useEffect, useState } from "react";
import style  from './recipes.module.css';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

const RecipeItem = ({title, calories, image, ingredients}) => {
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

  return (
    <div className={style.wrapper}>
         {/* <div className={style.recipeStyle}> */}
        <div className={style.card}>
        <h1 className={style.titleStyle}>{title}</h1>
            {/* <p>{calories}</p> */}
            <img className={style.image} src={image} alt="" />
            <button className={style.card_btn}  onClick={handleShow}>View Recipe</button>
            <Modal show={show} onHide={handleClose} animation={true}>
            <Modal.Header closeButton>
            <Modal.Title className={style.modalTitle}>{title}</Modal.Title>
            </Modal.Header>
            <Modal.Body>
            <img className={style.modalImage} src={image} alt="" />
                <h4 className={style.titleStyle}>Ingredients</h4>
                <ul className={style.modalCentered}>
                {ingredients.map(ing => (
                    <li className={style.ingItems}>{ing.text}</li>
                ))}
            </ul>
                </Modal.Body>
            <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
            Close
            </Button>
            </Modal.Footer>
        </Modal>   
        </div>
    </div>
  )
}

export default RecipeItem;
