import React from 'react'
import './modal.css';


const Modal = props => {
    if(!props.show){
        return null
    }

    const ingmodal = ({ingredients}) => {
        <ul>
                {ingredients.map(ing => (
                    <li className='ingItems'>{ing.text}</li>
                ))}
            </ul>
    }

  return (
    <div className='modal'>
        <div className='modal-content'>
            <div className='modal-header'>
                <h4 className='modal-title'>Modal title</h4>
            </div>
            <div className='modal-body'>
            <div className='ingStyle'>
            <h5 className='ingText'>Ingredients</h5>
            <div ingmodal={ingmodal}></div>
            </div>
            </div>
            <div className='modal-footer'>
                <button onClick={props.onClose} className='button'>Close</button>
            </div>
        </div>
    </div>
  )
}

export default Modal
