import React from 'react'
import style  from './recipes.module.css';

const RecipeItem = ({title, calories, image, ingredients}) => {


  function handleSubmit(e) {
    e.preventDefault();
    console.log('You clicked submit.');
  }

  return (
    <div className={style.wrapper}>
         {/* <div className={style.recipeStyle}> */}
        <div className={style.card}>
        <h1 className={style.titleStyle}>{title}</h1>
            {/* <p>{calories}</p> */}
            <img className={style.image} src={image} alt="" />
            <button onClick={() => this.setState({showModal: true})} className={style.card_btn}>View Recipe</button>
            <Modal open={this.state.showModal}>
            <div className={style.ingStyle}>
            <h5 className={style.ingText}>Ingredients</h5>
            <ul>
                {ingredients.map(ing => (
                    <li className={style.ingItems}>{ing.text}</li>
                ))}
            </ul>
            </div>
            </Modal>
        </div>
        {/* </div> */}
    </div>
  )
}


export default RecipeItem;
