import React, { useEffect, useState } from "react";
import style  from './recipes.module.css';
// import Example from '../src/Modal';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

const RecipeItem = ({title, calories, image, ingredients}) => {
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

  return (
    <div className={style.wrapper}>
         {/* <div className={style.recipeStyle}> */}
        <div className={style.card}>
        <h1 className={style.titleStyle}>{title}</h1>
            {/* <p>{calories}</p> */}
            <img className={style.image} src={image} alt="" />
            <button className={style.card_btn}  onClick={handleShow}>View Recipe</button>
            {/* <Example /> */}

            <Modal show={show} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Modal heading</Modal.Title>
          </Modal.Header>
          <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Close
            </Button>
            <Button variant="primary" onClick={handleClose}>
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>

            {/* <Modal open={this.state.showModal}>
            <div className={style.ingStyle}>
            <h5 className={style.ingText}>Ingredients</h5>
            <ul>
                {ingredients.map(ing => (
                    <li className={style.ingItems}>{ing.text}</li>
                ))}
            </ul>
            </div>
            </Modal> */}
        </div>
        {/* </div> */}
    </div>
  )
}

export default RecipeItem;
