import React from 'react'
import style from './recipe.module.css';

const RecipeItem = ({title, calories, image, ingredients}) => {
  return (
    <div>
      <h1 className='{style.recipeStyle}'>{title}</h1>
      <ol>
        {ingredients.map(ing => (
            <li>{ing.text}</li>
        ))}
      </ol>
      <p>{calories}</p>
      <img src={image} alt="" />

    </div>
  )
}

export default RecipeItem;
