import React, { useState } from 'react'

function Modal({ingredients}) {
    const [toggle, setToggle] = useState(true);
    const handleClick = () => {
        setToggle(!toggle)
      };

      if (toggle) {
        return (
          <div>
            <button onClick={handleClick} className="btn btn-dark mb-5">Toggle State</button>
            <h5 className='ingText'>Ingredients</h5>
            <ul>
                {ingredients.map(ing => (
                    <li className='ingItems'>{ing.text}</li>
                ))}
            </ul>
          </div>
        )
      } else {
        return <button onClick={handleClick} className="btn btn-dark mb-5">Toggle State</button>
      }
    }

export default Modal
